def calculate_differences(input_list):
    # 创建一个空列表用于存储差值
    differences = []

    # 遍历列表，计算相邻元素的差值
    for i in range(1, len(input_list)):
        difference = input_list[i] - input_list[i - 1]  # 当前元素减去前一个元素
        differences.append(difference)

    return differences
