for i in range(58):
    tou_seq = consistent_tou_direct[i]
    yan_seq = consistent_yan_direct[i]

    tou_encoder = KBinsDiscretizer(n_bins=6, encode='ordinal', strategy='quantile')
    tou_discretized = tou_encoder.fit_transform(np.array(tou_seq).reshape(-1, 1)).flatten()
    yan_encoder = KBinsDiscretizer(n_bins=6, encode='ordinal', strategy='quantile')
    yan_discretized = yan_encoder.fit_transform(np.array(yan_seq).reshape(-1, 1)).flatten()

    # 计算离散化序列之间的互信息
    mi_score = mutual_info_score(tou_discretized, yan_discretized)

    # 将结果添加到列表中
    CONSISTENT_MUTUL_ENTROPY.append(mi_score)

for i in range(62):
    tou_seq = NONconsistent_tou_direct[i]
    yan_seq = NONconsistent_yan_direct[i]

    tou_encoder = KBinsDiscretizer(n_bins=6, encode='ordinal', strategy='quantile')
    tou_discretized = tou_encoder.fit_transform(np.array(tou_seq).reshape(-1, 1)).flatten()
    yan_encoder = KBinsDiscretizer(n_bins=6, encode='ordinal', strategy='quantile')
    yan_discretized = yan_encoder.fit_transform(np.array(yan_seq).reshape(-1, 1)).flatten()

    # 计算离散化序列之间的互信息
    mi_score = mutual_info_score(tou_discretized, yan_discretized)

    # 将结果添加到列表中
    NONCONSISTENT_MUTUL_ENTROPY.append(mi_score)




mean_consistent = np.mean(CONSISTENT_MUTUL_ENTROPY)
std_consistent = np.std(CONSISTENT_MUTUL_ENTROPY, ddof=1)  # 样本标准差

mean_nonconsistent = np.mean(NONCONSISTENT_MUTUL_ENTROPY)
std_nonconsistent = np.std(NONCONSISTENT_MUTUL_ENTROPY, ddof=1)  # 样本标准差

